def isbst_mainfunc(root):
    #using inorder travers
    global prev

    if root is None:
        return True

    if isbst_mainfunc(root.left) is False:
        return False

    if prev is not None and prev.value > root.value:
        return False

    prev = root
    return isbst_mainfunc(root.right)

