def insert(root, a):
    current_node = root
    b = []
    i = 64
    while i > 0:
        b.append(a%2)
        a = a//2
        i -= 1

    b.reverse()
    for bit in b:
        if bit == 0:
            if current_node.zero == None:
                current_node.zero = Node(0)
                current_node = current_node.zero
            else:
                current_node = current_node.zero
        else:
            if current_node.one == None:
                current_node.one = Node(0)
                current_node = current_node.one
            else:
                current_node = current_node.one
    current_node.cnt += 1

def find_max_xor(root, a):
    current_node = root
    b = []
    i = 64
    while i > 0:
        b.append(a%2)
        a //= 2
        i -= 1
    b.reverse()
    ans = 0
    for bit in b:
        if bit == 0:
            if current_node.one == None:
                current_node = current_node.zero
                ans = ans*2
            else:
                current_node = current_node.one
                ans = ans*2 + 1
        else:
            if current_node.zero == None:
                current_node = current_node.one
                ans = ans*2
            else:
                current_node = current_node.zero
                ans = ans*2 + 1

    return  ans

