def trieInsert(string):
    current = root
    for c in string:
        tmp_char = ord(c) - ord('a')
        tmp_digit = digit[tmp_char]
        if current.next[tmp_digit] == None:
            current.next[tmp_digit] = Node()
            current = current.next[tmp_digit]
        else:
            current = current.next[tmp_digit]
    current.cnt = current.cnt + 1

