        j = len(keyset) - i - 1
        current = root
        k = j
        dp[j] = 999999
        while k < len(keyset) and current is not None:
            current = current.next[keyset[k]]
            if current is None:
                break
            if current.cnt > 0 and dp[k+1] != -1:
                dp[j] = min(dp[j], 1 + dp[k+1])
            k = k + 1
        if dp[j] == 999999:
            dp[j] = -1
    return dp[0]

