def data_structure(li):
    x = Tree()
    output = []
    for cmd in li:
        if cmd[0] == 1:
            num = int(cmd[1])
            amount = int(cmd[2])
            x.increase_value(num, amount)
        else:
            num = int(cmd[1])
            output.append(x.sum_range_zero(My_pair(num, None)))
    return output

