from rb_tree import RedBlackTree

class My_pair:
    def __init__(self, key, value):
        self.key = key
        self.value = value

    def increment_value(self):
        self.value += 1

    def __lt__(self, other):
        return self.key < other.key

    def __le__(self, other):
        return self.key <= other.key

    def __gt__(self, other):
        return self.key > other.key

    def __ge__(self, other):
        return self.key >= other.key

    def __eq__(self, other):
        if self is None or other is None:
            return self is None and other is None
        return self.key == other.key

    def __ne__(self, other):
        return not self == other


#li elements are in (a, b, h) form
def func(li):
    li.sort(key=lambda x:x[1])
    tree = RedBlackTree()
    for a, h, b in li:
        prev_b = tree.floor(My_pair(b, None))
        if prev_b is None:
            height_b = 0
        else:
            height_b = prev_b.value

        next_a = tree.ceil(My_pair(a, None))
        while (next_a is not None) and next_a.key <= b:
            tree.remove(next_a)
            next_a = tree.ceil(My_pair(a, None))

        tree.add(My_pair(a, h))
        tree.add(My_pair(b, height_b))

    prev_height = 0
    ans = []
    for element in tree:
        if element.value != prev_height:
            prev_height = element.value
            ans.append((element.key, element.value))
    return ans

def make_tree(ind, l, r, node, a):
    if (l == r - 1):
        node[ind] = a[l]
        return
    mid = (l + r) // 2
    make_tree(ind * 2, l, mid, node, a)
    make_tree(ind * 2 + 1, mid, r, node, a)
    node[ind] = min(node[ind * 2], node[ind * 2 + 1])


def get_min(ind, l, r, ql, qr, node):  # ql, qr determine segment of query
    if (qr <= l or r <= ql):
        return inf
    if (ql <= l and r <= qr):
        return node[ind]
    mid = (l + r) // 2
    return min(get_min(ind * 2, l, mid, ql, qr, node)
               , get_min(ind * 2 + 1, mid, r, ql, qr, node))

