def bfs(g,u):
        distance = [-1]*g.v
        distance[u] = 0
        q = []
        q.append(u)
        while(len(q) > 0):
            t = q.pop()
            for v in g.graph[t]:
                if(distance[v] == -1):
                    q.append(v)
                    distance[v] = distance[t] + 1

        return max(distance)

